import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

const Replies = () => {
    const [replyList, setReplyList] = useState([]);
    const [reply, setReply] = useState("");
    const [title, setTitle] = useState("");
    const navigate = useNavigate();
    const { id } = useParams();

    useEffect(() => {
        const fetchReplies = () => {
            fetch("http://localhost:8081/api/thread/replies", {
                method: "POST",
                body: JSON.stringify({
                    id,
                }),
                headers: {
                    "Content-Type": "application/json",
                },
            })
                .then((res) => res.json())
                .then((data) => {
                    setReplyList(data.replies);
                    setTitle(data.title);
                })
                .catch((err) => console.error(err));
        };
        fetchReplies();
    }, [id]);

    //üëáüèª This function when triggered when we add a new reply
    const addReply = () => {
        fetch("http://localhost:8081/api/create/reply", {
            method: "POST",
            body: JSON.stringify({
                id,
                userId: localStorage.getItem("_id"),
                reply
            }),
            headers: {
                "Content-Type": "application/json"
            },
        })
            .then((res) => res.json())
            .then((data) => {
                alert(data.message);
                navigate("/dashboard");
            })
            .catch((err) => console.error(err));
    };
    
    const handleSubmitReply = (e) => {
        e.preventDefault();
        //üëáüèª calls the function
        addReply();
        setReply("");
    };


    return (
    <main className='replies'>
        <nav>
                    <img src={logo} className="logo"></img>
                    <div className="navbuttons">
                    {localStorage.getItem("_id") !== null && 
                        <button className="btn" onClick={signOut}>
                            SIGN OUT
                        </button>
                        
                    }
                    {username && <h1>{username}</h1>}
                    {localStorage.getItem("_id") === null && 
                        <button className="btn" onClick={gotolog}>
                            LOG IN
                        </button>
                    }
                    {localStorage.getItem("_id") === null && 
                        <button className="btn" onClick={gotosign}>
                            SIGN IN
                        </button>
                    }</div>
                </nav>
        <h1 className='repliesTitle'>{title}</h1>

        <form className='modal__content' onSubmit={handleSubmitReply}>
            <label htmlFor='reply'>Reply to the thread</label>
            <textarea
                rows={5}
                value={reply}
                onChange={(e) => setReply(e.target.value)}
                type='text'
                name='reply'
                className='modalInput'
            />

            <button className='modalBtn'>SEND</button>
        </form>

        <div className='thread__container'>
            {replyList.map((reply) => (
                <div className='thread__item'>
                    <p>{reply.text}</p>
                    <div className='react__container'>
                        <p style={{ opacity: "0.5" }}>by {reply.name}</p>
                    </div>
                </div>
            ))}
        </div>
    </main>
);
};



export default Replies;
