import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import Validation from './RegisterValidation';
import axios from 'axios';


const Register = () => {
    const [values,setValues] =useState({
        name: '',
        email: '',
        password: '',
        confpassword: ''
    })
    const navigate =useNavigate();
    const [errors, serErrors] = useState({})
    const handleInput = (e) => {
        setValues(prev => ({...prev, [e.target.name]: [e.target.value]}))
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        serErrors(Validation(values));
        if(errors.name==='' && errors.password ==='' && errors.email === '' && errors.confpassword ===''){
            axios.post('http://localhost:8081/signup', values)
            .then(res => {
                navigate('/')
            })
            .catch(err => console.log(err));
        }
    };

    
    return (
        <main className='register'>
            <nav>
                <img src="zdjlogo.img" class="logo"></img>
            </nav>
            <div class="space"></div>
            <div class='registerf'>
                
            <h1 className='registerTitle'>Create an account</h1>
            <form className='registerForm' onSubmit={handleSubmit}>
                <label htmlFor='username'>Username</label>
                <input
                placeholder="YourUsername" 
                    type='text'
                    name='name'
                    id='name'
                    onChange={handleInput}
                />
                {errors.name && <span>{errors.name}</span>}
                <br></br>
                <div class="break"></div>
                <label htmlFor='email'>Email Address</label>
                <input
                    placeholder="examplemail@gmail.com"
                    type='text'
                    name='email'
                    id='email'
                    onChange={handleInput}
                />
                {errors.email && <span>{errors.email}</span>}
                <br></br>
                <div class="break"></div>
                <label htmlFor='password'>Password</label>
                <input
                    placeholder="*********"
                    type='password'
                    name='password'
                    id='password'
                    onChange={handleInput}
                    
                />
                {errors.password && <span>{errors.password}</span>}
                <br></br>
                <div class="break"></div>
                <label htmlFor='confpassword'>Confirm password</label>
                <input
                    placeholder="*********"
                    type='confpassword'
                    name='confpassword'
                    id='confpassword'
                    onChange={handleInput}
                />
                {errors.confpassword && <span>{errors.confpassword}</span>}
                <br></br>
                <div class="break"></div>
                <button onlick="handlesubmit()" type='submit' className='registerBtn'>REGISTER</button>
                <p>
                    Have an account? <Link to='/'>Sign in</Link>
                </p>
            </form>
            </div>
        </main>
    );
};

export default Register;
